object ultimate extends App {  object Calculator {    def apply(): Calculator = new Calculator()  };  class Calculator() {    println("Calc constructor");    //Scala will compile any code you place in the class body which isn't part of a field or a method definition to primary constructor.    def add(x: Int, y: Int): Integer = x + y;    def subtract(x: Int, y: Int): Integer = x + y;    def multiply(x: Int, y: Int): Integer = x * y;    def divide(x: Int, y: Int): Integer = x / y;  };  val calc = Calculator();  println(calc.multiply(69, 6));};