import scala.util.control._;object DeleteAndSort {  val outer = new Breaks;  def main(args: Array[String]): Unit = {  };  def minDeletionSize(A: Array[String]): Int = {    var count = 0;for (j <- 0 until A(0).size) {      outer.breakable {        for (i <- 0 until A.size-1) {          if (A(i)(j) > A(i + 1)(j)) {            count += 1;            outer.break;          };        };      };    };    count;  };  println(minDeletionSize(Array[String]("rrjk", "furt", "guzm")));}