class RecentCounter() {  var queue = scala.collection.mutable.Queue[Int]();  def ping(t: Int): Int = {    queue += t;    queue.dequeueAll(_ <= t - 3000);      queue.size;  };};object RecentCounter {  def main(args: Array[String]): Unit = {    val recentCounter = new RecentCounter();    val firstHit = recentCounter.ping(1);    println(  recentCounter.ping(2));    val secondHit = recentCounter.ping(1000);    val thirdHit = recentCounter.ping(3000);    println(  recentCounter.ping(3001));    println(  recentCounter.ping(3002));  }}