import scala.collection.mutable.ListBuffer;import scala.util.control._;object Self_Dividing_Numbers {  val outloop = new Breaks;  def main(args: Array[String]): Unit = {    println(selfDividingNumbers(47, 85).mkString(","));  };  def selfDividingNumbers(i: Int, j: Int): List[Int] = {    var list = ListBuffer[Int]();      for (m <- i to j) {        list += m;        outloop.breakable{          for (c <- m.toString.toCharArray) {            if (c.toString.toInt == 0 || (m % c.toString.toInt) > 0) {              list -= m;              outloop.break();            };          };        }      };    list.toList;  };}