import scala.util.control.Breaks._;import scala.util.control._;object break {  val outloop = new Breaks;  def main(args: Array[String]): Unit = {   // breakCheck();    outloop.breakable    {      for (a <- 1 to 10)      {        if (a == 6)        // terminate the loop when        // the value of a is equal to 6          outloop.break();        else          println(a);      }    }  };  def breakCheck(): Unit = {    var x = 5;breakable{  for (i <- 0 to 10) {    if (i == x)      break;    else println(i);  };}  };};