object mapDemo extends App {  //captilatize first letter  val names = List("aaa", "bbb", "cccccc", "dd");  val capNames = names.map(_.capitalize);  println(capNames);  //multiply all numbers by 2  val nums = Array(2, 3, 4, 5, 6);  val doubles = nums.map(_ * 2);  println(doubles.mkString(","));  //boolean checks on numbers  val lessThanFive = nums.map(_ < 5);  println(lessThanFive.mkString(","));  //powerful reduce function  println(nums.reduce(_ + _));  println(nums.sum); //same output as aBove}