object selfDividingViaFilters {  def main(args: Array[String]): Unit = {    println(selfDividingNumbers(0, 85));  };  def selfDividingNumbers(m: Int, n: Int): List[Int] = {    val list = (m to n)      .filter(number =>        number.toString.map(digit => digit.toString.toInt)          .filter(digit => digit == 0 || number % digit != 0).size == 0).toList;    list;  };}