object traits {};//traits mixins , using traitstrait life {  def live() {    println("im living man!!");  };  def death();};trait move {  def run();  def walk();};class Human(name: String) extends life with move {  override def death(): Unit = println("Dead!");  override def run(): Unit = println("Running!");  override def walk(): Unit = println("Walking!");};object Human extends App {  val human = new Human("Kishan");  human.live();  human.run();  human.walk();  human.death();}